import React, { useState } from "react";
import NewExpence from "./Component/NewExpence/NewExpence";
import ExpensesItem from "./Component/ExpencesItem";
import "./Component/Expence.css";
import ExpensesFilter from "./Component/NewExpence/ExpenceFilter";

const expenses = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const App = () => {
  const [Expenses, setExpenses] = useState(expenses);
  const addExpenseHandler = (expenses) => {
    console.log(expenses);
    console.log(preExpence);
    setExpenses((preExpence) => {
      return [expenses, ...preExpence];
    });
  };

  return (
    <div>
      <NewExpence onAddExpense={addExpenseHandler} />
      <div className="secbox">
        <card className="expenses">
          <ExpensesFilter />
          <ExpensesItem
            title={expenses[0].title}
            amount={expenses[0].amount}
            date={expenses[0].date}
          />
          <ExpensesItem
            title={expenses[1].title}
            amount={expenses[1].amount}
            date={expenses[1].date}
          />
          <ExpensesItem
            title={expenses[2].title}
            amount={expenses[2].amount}
            date={expenses[2].date}
          />
          <ExpensesItem
            title={expenses[3].title}
            amount={expenses[3].amount}
            date={expenses[3].date}
          />
        </card>
      </div>
    </div>
  );
};

export default App;
